import React,{ Component } from 'react';
import axios from 'axios';
import {
	Form, Select,Input, InputNumber, Button, Upload, Icon
  } from 'antd';

const { Option } = Select;

class ProductDatail extends Component {
	constructor(props) {
		super(props);
		this.state = {
			id: this.props.match.params.id,
			data: ''
		}
	}
	componentDidMount() {
		this.loadProduct();
	}
	loadProduct() {
		if(this.state.id) {
			axios.get('/api/save.json?id='+this.state.id).then((res)=>{
				let data = res.data;
				console.log(data)
				if(data.success==true){
					this.setState({
						data: data.data
					})
				}
			}).catch((err)=>{
				console.log(err)
			})
		}
	}
	normFile = e => {
		console.log('Upload event:', e);
		if (Array.isArray(e)) {
		  return e;
		}
		return e && e.fileList;
	  };
	render() {
		const { getFieldDecorator } = this.props.form;
		const formItemLayout = {
		labelCol: { span: 6 },
		wrapperCol: { span: 14 },
		};
		return (
			<Form {...formItemLayout} onSubmit={this.handleSubmit}>
				<Form.Item label="商品名称" {...formItemLayout}>
					<Input placeholder="请输入商品名称" value={this.state.data.name} />
				</Form.Item>
				<Form.Item label="商品描述" {...formItemLayout}>
					<Input placeholder="请输入商品描述" value={this.state.data.details}/>
				</Form.Item>
				<Form.Item label="商品价格">
				{getFieldDecorator('mon-number', { initialValue: this.state.data.price })(<InputNumber min={0} max={9999999} />)}
				<span className="ant-form-text"> 元</span>
				</Form.Item>
				<Form.Item label="商品库存">
				<span className="ant-form-text">{this.state.data.number} 件</span>
				</Form.Item>
      		</Form>
		)
	}
}

const WrappedProductDatail = Form.create()(ProductDatail);

export default WrappedProductDatail;
