import React,{ Component } from 'react';
import { Link } from 'react-router-dom';
import { Table, Divider, Tag, Popconfirm, Select  } from 'antd';

const { Column, ColumnGroup } = Table;
const { Option } = Select;

class ProductList extends Component {
	constructor() {
		super();
		this.state = {
			data: [
				{
				  key: '1',
				  firstName: 'John',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: 1,
				},
				{
				  key: '2',
				  firstName: 'Jim',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: 2
				},
				{
				  key: '3',
				  firstName: 'Joe',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: 1
				},{
				  key: '4',
				  firstName: 'John',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: 1
				},
				{
				  key: '5',
				  firstName: 'Jim',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: 2
				},
				{
				  key: '6',
				  firstName: 'Joe',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: 1
				},{
				  key: '7',
				  firstName: 'John',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: 1
				},
				{
				  key: '8',
				  firstName: 'Jim',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: 2
				},
				{
				  key: '9',
				  firstName: 'Joe',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: 1
				},{
				  key: '10',
				  firstName: 'John',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: 1
				},
				{
				  key: '11',
				  firstName: 'Jim',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: 2
				},
				{
				  key: '12',
				  firstName: 'Joe',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: 2
				}
			  ]
		}
	}
	render() {
		return (
			<div>
				<div></div>
				<Table dataSource={this.state.data}>
					<Column title="id" dataIndex="firstName" key="firstName" />
					<Column title="价格" dataIndex="age" key="age" />
					<Column title="商品详情" dataIndex="address" key="address" />
					<Column
					title="状态"
					dataIndex="tags"
					key="tags"
					render={tags => (
						<span>
							<Tag color="blue" key={tags}>
							{tags===1?'在售':'下架'}
							</Tag>
						</span>
					)}
					/>
					<Column
					title="操作"
					key="action"
					render={(text, record) => (
						<span>
						<Link tp={`/product/detail/${record.key}`}>详情</Link>
						<Divider type="vertical" />
						<Link tp={`/product/save/${record.key}`}>编辑</Link>
						</span>
					)}
					/>
  				</Table>
			</div>
		)
	}
}

export default ProductList;
