import React,{ Component } from 'react';
import { Table, Divider, Tag, Popconfirm } from 'antd';

const { Column, ColumnGroup } = Table;


class ProductList extends Component {
	constructor() {
		super();
		this.state = {
			data: [
				{
				  key: '1',
				  firstName: 'John',
				  lastName: 'Brown',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: 1,
				},
				{
				  key: '2',
				  firstName: 'Jim',
				  lastName: 'Green',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: ['loser']
				},
				{
				  key: '3',
				  firstName: 'Joe',
				  lastName: 'Black',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: ['cool', 'teacher']
				},{
				  key: '4',
				  firstName: 'John',
				  lastName: 'Brown',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: ['nice', 'developer']
				},
				{
				  key: '5',
				  firstName: 'Jim',
				  lastName: 'Green',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: ['loser']
				},
				{
				  key: '6',
				  firstName: 'Joe',
				  lastName: 'Black',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: ['cool', 'teacher']
				},{
				  key: '7',
				  firstName: 'John',
				  lastName: 'Brown',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: ['nice', 'developer']
				},
				{
				  key: '8',
				  firstName: 'Jim',
				  lastName: 'Green',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: ['loser']
				},
				{
				  key: '9',
				  firstName: 'Joe',
				  lastName: 'Black',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: ['cool', 'teacher']
				},{
				  key: '10',
				  firstName: 'John',
				  lastName: 'Brown',
				  age: 32,
				  address: 'New York No. 1 Lake Park',
				  tags: ['nice', 'developer']
				},
				{
				  key: '11',
				  firstName: 'Jim',
				  lastName: 'Green',
				  age: 42,
				  address: 'London No. 1 Lake Park',
				  tags: ['loser']
				},
				{
				  key: '12',
				  firstName: 'Joe',
				  lastName: 'Black',
				  age: 32,
				  address: 'Sidney No. 1 Lake Park',
				  tags: ['cool', 'teacher']
				}
			  ]
		}
	}
	handleAdd = () => {
		const { data } = this.state;
		const newData = {
		  key: 100,
		  name: `Edward King ${count}`,
		  age: 32,
		  address: `London, Park Lane no. ${count}`,
		};
		this.setState({
		  dataSource: [...dataSource, newData],
		});
	  };
	render() {
		return (
			<Table dataSource={this.state.data}>
				<Column title="id" dataIndex="firstName" key="firstName" />
				<Column title="价格" dataIndex="age" key="age" />
				<Column title="商品详情" dataIndex="address" key="address" />
				<Column
				title="状态"
				dataIndex="tags"
				key="tags"
				render={tags => (
					<span>
						<Tag color="blue" key={tags}>
						{tags===1?'在售':'下架'}
						</Tag>
					</span>
				)}
				/>
				<Column
				title="操作"
				key="action"
				render={(text, record) => (
					<span>
					<a href="javascript:;">Invite {record.lastName}</a>
					<Divider type="vertical" />
					<Popconfirm title="确定删除？" onConfirm={() => this.handleDelete(record.key)}>
					<a href="javascript:;">Delete</a>
					</Popconfirm>
					</span>
				)}
				/>
  			</Table>
		)
	}
}

export default ProductList;
