import React,{ Component } from 'react';
import axios from 'axios';
import { Form } from 'antd';


class OrderDatail extends Component {
	constructor(props) {
		super(props);
		this.state = {
			id: this.props.match.params.id,
			data: ''
		}
	}
	componentDidMount() {
		this.loadProduct();
	}
	loadProduct() {
		if(this.state.id) {
			axios.get('/api/save.json?id='+this.state.id).then((res)=>{
				let data = res.data;
				console.log(data)
				if(data.success===true){
					this.setState({
						data: data.data
					})
				}
			}).catch((err)=>{
				console.log(err)
			})
		}
	}
	normFile = e => {
		console.log('Upload event:', e);
		if (Array.isArray(e)) {
		  return e;
		}
		return e && e.fileList;
	  };
	render() {
		const formItemLayout = {
		labelCol: { span: 6 },
		wrapperCol: { span: 14 },
		};
		return (
			<Form {...formItemLayout} onSubmit={this.handleSubmit}>
				<Form.Item label="商品名称" {...formItemLayout}>
					{this.state.data.name}
				</Form.Item>
				<Form.Item label="商品描述" {...formItemLayout}>
					{this.state.data.details}
				</Form.Item>
				<Form.Item label="商品价格">
				<span className="ant-form-text">{this.state.data.price} 元</span>
				</Form.Item>
				<Form.Item label="商品库存">
				<span className="ant-form-text">{this.state.data.number} 件</span>
				</Form.Item>
      		</Form>
		)
	}
}

const WrappedOrderDatail = Form.create()(OrderDatail);

export default WrappedOrderDatail;
