import React,{ Component } from 'react';
import {
	Form, Select,Input, InputNumber, Switch, Radio, Button, Upload, Icon
  } from 'antd';

const { Option } = Select;

class ProductSave extends Component {
	normFile = e => {
		console.log('Upload event:', e);
		if (Array.isArray(e)) {
		  return e;
		}
		return e && e.fileList;
	  };
	render() {
		const { getFieldDecorator } = this.props.form;
		const formItemLayout = {
		labelCol: { span: 6 },
		wrapperCol: { span: 14 },
		};
		return (
			<Form {...formItemLayout} onSubmit={this.handleSubmit}>
				<Form.Item label="商品名称" {...formItemLayout}>
					<Input placeholder="请输入商品名称" />
				</Form.Item>
				<Form.Item label="商品描述" {...formItemLayout}>
					<Input placeholder="请输入商品描述" />
				</Form.Item>
				<Form.Item label="商品价格">
				{getFieldDecorator('input-number', { initialValue: 3 })(<InputNumber min={0} max={9999999} />)}
				<span className="ant-form-text"> 元</span>
				</Form.Item>
				<Form.Item label="商品库存">
				{getFieldDecorator('input-number', { initialValue: 3 })(<InputNumber min={1} max={10} />)}
				<span className="ant-form-text"> 件</span>
				</Form.Item>

				<Form.Item label="Upload">
				{getFieldDecorator('upload', {
					valuePropName: 'fileList',
					getValueFromEvent: this.normFile,
				})(
					<Upload name="logo" action="/upload.do" listType="picture">
					<Button>
						<Icon type="upload" /> Click to upload
					</Button>
					</Upload>,
				)}
				</Form.Item>
				<Form.Item wrapperCol={{ span: 12, offset: 6 }}>
				<Button type="primary" htmlType="submit">
					Submit
				</Button>
				</Form.Item>
      		</Form>
		)
	}
}

const WrappedProductSave = Form.create()(ProductSave);

export default WrappedProductSave;
